import { students } from "@/data/gradeList"

export const GradeCard =() => {
    return(
        <table className="w-full border border-gray-600 rounded-md overflow-hidden">

            <thead>
                <tr className="text-left border-b border-gray-600 bg-gray-800">
                    <th className="p-3">Name</th>
                    <th className="p-3">Status</th>
                    <th className="p-3">Grade 1</th>
                    <th className="p-3">Grade 2</th>
                    <th className="p-3">Final Grade</th>
                </tr>
            </thead>

            
            {students.map(grade => (
                <tbody>
                    <tr className="text-gray-800 bg-gray-400 border-b border-gray-600" key={grade.id}>
                        <td className="p-3 flex items-center">
                            <img src="data:image/png;base64," 
                            alt={grade.name}
                            className="w-10 h-10 rounded-full mr-3"
                            />
                            <div>
                                <div className="font-bold">{grade.name}</div>
                                <div>{grade.email}</div>
                            </div>
                        </td>
                        <td className="p-3">
                            {grade.status !== 'active' && 
                                <div className="bg-red-600 px-2 w-[60px] py-1 inline-block rounded-md text-white text-xs">Inactive</div>
                            }

                            {grade.status === 'active' && 
                                <div className="bg-green-600 px-2 w-[60px] text-center py-1 inline-block rounded-md text-white text-xs">Active</div>
                            }
                        </td>

                        <td className="p-3">{grade.gradeOne.toFixed(1)}</td>
                        <td className="p-3">{grade.gradeTwo.toFixed(1)}</td>
                        <td className="p-3 font-bold">
                            {grade.status === "active" && ((grade.gradeOne + grade.gradeTwo) / 2).toFixed(1)}
                            {grade.status !== "active" &&  '--'}
                        </td>
                    </tr>
                </tbody>      
            ))}
        </table>
    )
}